import {useEffect, useState} from "react";
import ResultPage from "./ResultPage";
import StartPage from "./Startpage";

const Quiz = () => {

    const [quizData, setQuizData] = useState([]);

    useEffect(()=>{
        fetch("http://localhost:9090/quiz")
        .then(r => r.json())
        .then(j => setQuizData(j))
    },[]);

    console.log("quizData: ", quizData);


    const [currentQuestion, setCurrentQuestion] = useState(0);
    const [userAnswers, setUserAnswers] = useState([]);
    const [showResults, setShowResults] = useState(false);
    const [name, setName] = useState('');
    const [startedGame, setStartedGame] = useState(false);

    if(!quizData.length) return;

    const handleOptionClick = (selectedOption) => {
      setUserAnswers([...userAnswers, { question: currentQuestion, answer: selectedOption }]);
      if (currentQuestion + 1 < quizData.length) {
        setCurrentQuestion(currentQuestion + 1);
      } else {
        setShowResults(true);
        setStartedGame(false);
      }
    };

    const resetQuiz = () => {
      setCurrentQuestion(0);
      setUserAnswers([]);
      setShowResults(false);
    };

  return (
    <div>
      {showResults && <ResultPage userAnswers={userAnswers} resetQuiz={resetQuiz}/>}
      {!startedGame && <StartPage name={name} setName={setName}/>}
      {startedGame && <div>
        <h2>Quiz</h2>
        <p>{quizData[currentQuestion].question}</p>
        <ul>
          {quizData[currentQuestion].possibleAnswers.map((option, index) => (
            <li key={index} onClick={() => handleOptionClick(option)}>
              {option}
            </li>
          ))}
        </ul>
      </div>}
  </div>
  );
}

export default Quiz;
