import {useState} from "react";
import './QuizButton.css';



const AnswerOptions = 
	({
		setCorrectAnswers,
		setCurrentQuestion,
		currentQuestion,
		setUserAnswers,
		userAnswers,
		setStartGame,
		setShowResults,
		answers,
		correctAnswer,
		isLast
	}) =>
{

	const handleOptionClick = (selectedOption) => {
		setUserAnswers([...userAnswers, { question: currentQuestion, answer: selectedOption }]);
		const isCorrect = selectedOption === correctAnswer;

		setOptionClicked(true);


		setTimeout(() => {
			if (isCorrect) {
				setCorrectAnswers();
			}	

			if (isLast) {
				setCurrentQuestion();
			} else {
				setStartGame(false);
				setShowResults(true);
			}

			setOptionClicked(false);

		}, 3000)
	};

	const shuffleAnswers = () => {
			return answers.sort(()=> Math.random() - 0.5);
	}

	return (
		<div>
			{shuffleAnswers().map((option, index) => (
				<Option option={option} index={index} />
			))}
		</div>
	)
}

export default AnswerOptions;
