import {useState} from "react";
import ResultPage from "./ResultPage";
import StartPage from "./Startpage";
import QuizButton from "./Button";
const axios = require('axios');

const Quiz = ({quizData}) => {    
    const [currentQuestion, setCurrentQuestion] = useState(0);
    const [correctAnswers, setCorrectAnswers] = useState(0);
    const [userAnswers, setUserAnswers] = useState([]);
    const [showResults, setShowResults] = useState(false);
    const [name, setName] = useState('');
    const [startedGame, setStartedGame] = useState(false);

    if(!quizData.length) return;

    const handleOptionClick = (selectedOption) => {
      setUserAnswers([...userAnswers, { question: currentQuestion, answer: selectedOption }]);
      if(selectedOption == quizData[currentQuestion].correctAnswer){
        setCorrectAnswers(correctAnswers+1);
        console.log("Corr: ", correctAnswers);
      }
      if (currentQuestion + 1 < quizData.length) {
        setCurrentQuestion(currentQuestion + 1);
      } else {
        setShowResults(true);
        setStartedGame(false);
      }
    };

    const resetQuiz = () => {
      setCurrentQuestion(0);
      setUserAnswers([]);
      setShowResults(false);
    };

    const saveScore = async () => {
      /* await fetch('https://localhost:9090/highScore', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          "name": name,
          "score": correctAnswers
        }),
      }); */

      axios.post(`https://localhost:9090/highScore`, {"name":name, "score":correctAnswers}).then(r => console.log("r: ", r))

    }

  return (
    <div>
      {showResults && <ResultPage userAnswers={userAnswers} resetQuiz={resetQuiz} saveScore={saveScore} score={correctAnswers}/>}
      {!showResults && <StartPage name={name} setName={setName}/>}
      {!showResults && <div>
        <h2>Quiz</h2>
        <p>{quizData[currentQuestion].question}</p>
        <ul>
          {quizData[currentQuestion].possibleAnswers.map((option, index) => (
            <li key={index} style={{listStyleType:"none"}}>
              <QuizButton option={option} onClick={handleOptionClick}/>
              {/* <button style={{width:"100px", height:"30px"}}onClick={() => handleOptionClick(option)}>
                {option}
              </button> */}
            </li>
          ))}
        </ul>
      </div>}
  </div>
  );
}

export default Quiz;
