import './QuizButton.css';




const AnswerOptions = 
	({
		isLastQuestion,
		setCorrectAnswers,
		setCurrentQuestion,
		currentQuestion,
		setUserAnswers,
		userAnswers,
		setStartGame,
		setShowResults,
		answers
	}) => {

	const handleOptionClick = (selectedOption) => {
		setUserAnswers([...userAnswers, { question: currentQuestion, answer: selectedOption }]);

		/* if (isCorrect) {
				setCorrectAnswers();
		} */

		//answerFeedback(isCorrect);

		if (isLastQuestion) {
			setCurrentQuestion(currentQuestion + 1);
		} else {
			setStartGame(false);
			setShowResults(true);
		}

	};

	const shuffleAnswers = () => {
			return answers.sort(()=> Math.random() - 0.5);
	}

	return (
		<div>
			{shuffleAnswers().map((option, index) => (
				<li key={index} style={{ listStyleType: 'none' }}>
				<button className="quiz-button" onClick={() => handleOptionClick(option)}>
					{option}
				</button>
				</li>
			))}
		</div>
	)
}

export default AnswerOptions;
