import {useState} from "react";
import ResultPage from "./ResultPage";
import StartPage from "./Startpage";
import QuizButton from "./Button";

const Quiz = ({quizData}) => {    
    const [currentQuestion, setCurrentQuestion] = useState(0);
    const [correctAnswers, setCorrectAnswers] = useState(0);
    const [userAnswers, setUserAnswers] = useState([]);
    const [showResults, setShowResults] = useState(false);
    const [startGame, setStartGame] = useState(false);
    const [name, setName] = useState('');

    if(!quizData.length) return;

    const handleOptionClick = (selectedOption) => {
      setUserAnswers([...userAnswers, { question: currentQuestion, answer: selectedOption }]);
      if(selectedOption === quizData[currentQuestion].correctAnswer){
        setCorrectAnswers(correctAnswers+1);
      }
      if (currentQuestion + 1 < quizData.length) {
        setCurrentQuestion(currentQuestion + 1);
      } else {
        setShowResults(true);
      }
    };

    const resetQuiz = () => {
      setCurrentQuestion(0);
      setUserAnswers([]);
      setShowResults(false);
      setCorrectAnswers(0);
      setStartGame(true)
      setName('');
    };

    const shuffleAnswers = (answers) => {
      return answers.sort(()=> Math.random() - 0.5);
    }

  return (
    <div>
      {showResults && <ResultPage userAnswers={userAnswers} resetQuiz={resetQuiz} name={name} score={correctAnswers}/>}
      {!startGame && !showResults && <StartPage name={name} setName={setName}/>}
      {startGame && <div>
        <h2>Quiz</h2>
        <p>{quizData[currentQuestion].question}</p>
        <ul>
          {shuffleAnswers(quizData[currentQuestion].possibleAnswers).map((option, index) => (
            <li key={index} style={{listStyleType:"none"}}>
              <QuizButton option={option} onClick={handleOptionClick}/>
            </li>
          ))}
        </ul>
        <p>{`${currentQuestion +1} of ${quizData.length}`}</p>
      </div>}
  </div>
  );
}

export default Quiz;
