import {useState} from "react";
import './QuizButton.css';
import Option from "./Option";



const AnswerOptions = 
	({
		setCorrectAnswers,
		setCurrentQuestion,
		currentQuestion,
		setUserAnswers,
		userAnswers,
		setStartGame,
		setShowResults,
		answers,
		correctAnswer,
		isLast
	}) =>
{

	const handleOptionClick = (selectedOption) => {
		setUserAnswers([...userAnswers, { question: currentQuestion, answer: selectedOption }]);
		const isCorrect = selectedOption === correctAnswer;

		if (isCorrect) {
			setCorrectAnswers();
		}	

		if (isLast) {
			setCurrentQuestion();
		} else {
			setStartGame(false);
			setShowResults(true);
		}

	};

	const shuffleAnswers = () => {
			return answers.sort(()=> Math.random() - 0.5);
	}

	return (
		<div>
			{shuffleAnswers().map((option, index) => (
				<li
				key={index}
				style={{
					listStyleType: 'none',
					backgroundColor: "red"
				}}
			>

				<Option option={option} index={index} onClick={handleOptionClick}/>
			</li>
			))}
		</div>
	)
}

export default AnswerOptions;
