import {useState} from "react";



const AnswerOptions = 
	({
		setCorrectAnswers,
		setCurrentQuestion,
		currentQuestion,
		setUserAnswers,
		userAnswers,
		setStartGame,
		setShowResults,
		answers,
		correctAnswer,
		isLast
	}) =>
{

	const [optionClicked, setOptionClicked] = useState(false);

	const isCorrectOption = (op) => op === correctAnswer; 

	const handleOptionClick = (selectedOption) => {

		setOptionClicked(true);


		setTimeout(() => {
			setUserAnswers([...userAnswers, { question: currentQuestion, answer: selectedOption }]);
			const isCorrect = selectedOption === correctAnswer;

			if (isCorrect) {
				setCorrectAnswers();
			}	

			if (isLast) {
				setCurrentQuestion();
			} else {
				setStartGame(false);
				setShowResults(true);
			}

			setOptionClicked(false);
		}, 1000)

	};



	return (
		<div>
			{answers.map((option, index) => (
				<li
				key={index}
				style={{
					listStyleType: 'none'
				}}
			>
				<button
					disabled={optionClicked}
					className={`quiz-button ${optionClicked ? isCorrectOption(option) && "correct": ""}` }
					onClick={() => handleOptionClick()}
				>
						{option}
				</button>
			</li>
			))}
		</div>
	)
}

export default AnswerOptions;
